// Get reading for specific date
export const getReadingForDate = (month: number, day: number): McCheyneReading | undefined => {
  return mcCheyneReadingPlan.find(r => r.month === month && r.dayOfMonth === day);
};

// Get reading by day of year
export const getReadingByDay = (dayOfYear: number): McCheyneReading | undefined => {
  return mcCheyneReadingPlan.find(r => r.day === dayOfYear);
};

// Get current day reading based on today's date
export const getCurrentDayReading = (): McCheyneReading | undefined => {
  const today = new Date();
  const month = today.getMonth() + 1;
  const day = today.getDate();
  return getReadingForDate(month, day);
};

// Get current day number (1-365)
export const getCurrentDayNumber = (): number => {
  const today = new Date();
  const start = new Date(today.getFullYear(), 0, 0);
  const diff = today.getTime() - start.getTime();
  const oneDay = 1000 * 60 * 60 * 24;
  return Math.floor(diff / oneDay);
};

// Calculate reading progress
export const calculateReadingProgress = (
  checkedChapters: string[],
  totalChapters: string[]
): number => {
  if (totalChapters.length === 0) return 0;
  return Math.round((checkedChapters.length / totalChapters.length) * 100);
};
